import React, { useCallback } from 'react';
import { Form, Input, Button } from 'antd';
import Link from 'next/link';
import styled from 'styled-components';
import useInput from '../hooks/useInput';
import { useDispatch } from 'react-redux';

import { loginAction } from '../reducers';

const ButtonWrapper = styled.div`
  margin-top: 10px;
`;
const FormWrapper = styled(Form)`
  padding: 10px;
`;

const LoginForm = () => {
  const [ email, onChangeEmail ] = useInput('');
  const [ password, onChangePassword ] = useInput('');
  const dispatch = useDispatch();

  const onFinish = useCallback(() => {
    dispatch(loginAction(email));
  }, [email, password]);

  const onFinishFailed = useCallback(() => {

  }, []);

  return (
    <FormWrapper onFinish={onFinish} onFinishFailed={onFinishFailed} >
      <div>
        <label htmlFor="user-email">이메일</label>
        <br/>
        <Input type="email" name="user-email" value={email} onChange={onChangeEmail} required />
      </div>
      <div>
        <label htmlFor="user-password">비밀번호</label>
        <Input type="password" name="user-password" value={password} onChange={onChangePassword} required />
      </div>
      <ButtonWrapper>
        <Button type="primary" htmlType="submit" loading={false}>로그인</Button>
        <Link href="/signup"><a><Button>회원가입</Button></a></Link>
      </ButtonWrapper>
    </FormWrapper>
  );
}

export default LoginForm;
