import { useSelector } from 'react-redux';
import shortid from 'shortid';
const initialState = {
  mainPosts: [{
    id: shortid.generate(),
    User: {
      id: shortid.generate(),
      nickname: '제로초',
    },
    content: '첫 번째 게시글 #해쉬태그 #박범진 #개발',
    Images: [{
      id: shortid.generate(),
      src: 'https://bookthumb-phinf.pstatic.net/cover/137/995/13799585.jpg?udate=20180726',
    }, {
      id: shortid.generate(),
      src: 'https://gimg.gilbut.co.kr/book/BN001958/rn_view_BN001958.jpg',
    }, {
      id: shortid.generate(),
      src: 'https://gimg.gilbut.co.kr/book/BN001998/rn_view_BN001998.jpg',
    }],
    Comments: [{
      User: {
        id: shortid.generate(),
        nickname: 'nero',
      },
      content: '우와 개정판이 나왔군요~',
    }, {
      User: {
        id: shortid.generate(),
        nickname: 'hero',
      },
      content: '얼른 사고싶어요~',
    }]
  }],
  imagePaths: [],
  addPostLoading: false,
  addPostDone: false,
  addPostError: false,
  addCommentLoading: false,
  addCommentDone: false,
  addCommentError: false,
  removePostLoading: false,
  removePostDone: false,
  removePostError: false,
}

export const dummyPost = (data) => {
  return {
    id: shortid.generate(),
    content: data.content,
    User: {
      id: data.userId,
      nickname: data.nickname,
    },
    Images: [],
    Comments: [],
  };
};
export const dummyComment = (data) => {
  return {
    id: shortid.generate(),
    content: data.content,
    User: {
      id: data.userId,
      nickname: data.nickname,
    },

  };
};

export const ADD_POST_REQUEST = 'ADD_POST_REQUEST';
export const ADD_POST_SUCCESS = 'ADD_POST_SUCCESS';
export const ADD_POST_FAILURE = 'ADD_POST_FAILURE';

export const ADD_COMMENT_REQUEST = 'ADD_COMMENT_REQUEST';
export const ADD_COMMENT_SUCCESS = 'ADD_COMMENT_SUCCESS';
export const ADD_COMMENT_FAILURE = 'ADD_COMMENT_FAILURE';

export const REMOVE_POST_REQUEST = 'REMOVE_POST_REQUEST';
export const REMOVE_POST_SUCCESS = 'REMOVE_POST_SUCCESS';
export const REMOVE_POST_FAILURE = 'REMOVE_POST_FAILURE';

export const REMOVE_POST_OF_ME = 'REMOVE_POST_OF_ME';

export const addPost = (data) => {
  return ({
    type: ADD_POST_REQUEST,
    data: data,
  });
};

export const addComment = (data) => {
  return ({
    type: ADD_COMMENT_REQUEST,
    data: data
  });
};

export const removePost = (data) => {
  return ({
    type: REMOVE_POST_REQUEST,
    data: data
  });
};



const reducer = (state = initialState, action) => {
  switch (action.type) {
    case ADD_POST_REQUEST: {
      return {
        ...state,
        addPostLoading: true,
        addPostDone: false,
        addPostError: null,
      };
    }
    case ADD_POST_SUCCESS: {
      return {
        ...state,
        addPostLoading: false,
        addPostDone: true,
        mainPosts: [action.data, ...state.mainPosts],
      };
    }
    case ADD_POST_FAILURE: {
      return {
        ...state,
        addPostLoading: false,
        addPostDone: false,
        addPostError: action.error,
      };
    }
    case ADD_COMMENT_REQUEST: {
      return {
        ...state,
        addCommentLoading: true,
        addCommentDone: false,
        addCommentError: null,
      };
    }
    case ADD_COMMENT_SUCCESS: {
      const postIndex = state.mainPosts.findIndex((v) => v.id === action.data.postId);
      const post = {...state.mainPosts[postIndex]};
      post.Comments = [dummyComment(action.data), ...post.Comments];
      const mainPosts = [...state.mainPosts];
      mainPosts[postIndex] = post;
      return {
        ...state,
        addCommentLoading: false,
        addCommentDone: true,
        mainPosts
      };
    }
    case ADD_COMMENT_FAILURE: {
      return {
        ...state,
        addCommentLoading: false,
        addCommentDone: false,
        addCommentError: action.error,
      };
    }
    case REMOVE_POST_REQUEST: {
      return {
        ...state,
        removeoPostLoading: true,
        removeoPostDone: false,
        removeoPostError: null,
      };
    }
    case REMOVE_POST_SUCCESS: {
      return {
        ...state,
        removeoPostLoading: false,
        removeoPostDone: true,
        mainPosts : state.mainPosts.filter((v) => v.id !== action.data.post.id)
      };
    }
    case REMOVE_POST_FAILURE: {
      return {
        ...state,
        removeoPostLoading: false,
        removeoPostDone: false,
        removeoPostError: action.error,
      };
    }

    default:
      return state;
  }
}

export default reducer;
