import { actionChannel, all, call, delay, fork, put, takeLatest } from 'redux-saga/effects';
import axios from 'axios';

import { 
  ADD_POST_REQUEST, ADD_POST_SUCCESS, ADD_POST_FAILURE, 
  ADD_COMMENT_REQUEST, ADD_COMMENT_SUCCESS, ADD_COMMENT_FAILURE,
  REMOVE_POST_REQUEST, REMOVE_POST_SUCCESS, REMOVE_POST_FAILURE, dummyPost, 
} from '../reducers/post';
import { ADD_POST_TO_ME } from '../reducers/user';

function addPostAPI() {
  return axios.post('/api/addPost');
}

function* addPost(action) {
  try {
    // const result = yield call(addPostAPI); 
    yield delay(1000);
    const newPost = dummyPost(action.data);
    yield put({
      type: ADD_POST_SUCCESS,
      data: newPost
    });
    yield put({
      type: ADD_POST_TO_ME,
      data: newPost
    });
  } catch (err) {
    yield put({
      type: ADD_POST_FAILURE,
      error: err.response.data

    });
  }
}
function addCommentAPI() {
  return axios.post('/api/addComment');
}

function* addComment(action) {
  try {
    // const result = yield call(addCommentAPI); 
    yield delay(1000);
    yield put({
      type: ADD_COMMENT_SUCCESS,
      data: action.data
    });
  } catch (err) {
    yield put({
      type: ADD_COMMENT_FAILURE,
      error: err.response.data

    });
  }
}

function removePostAPI() {
  return axios.post('/api/removePost');
}

function* removePost(action) {
  try {
    // const result = yield call(removePostAPI); 
    yield delay(1000);
    yield put({
      type: REMOVE_POST_SUCCESS,
      data: action.data
    });
  } catch (err) {
    yield put({
      type: REMOVE_POST_FAILURE,
      error: err.response.data

    });
  }
}

function* watchAddPost() {
  yield takeLatest(ADD_POST_REQUEST, addPost);
}
function* watchAddComment() {
  yield takeLatest(ADD_COMMENT_REQUEST, addComment);
}
function* watchRemovePost() {
  yield takeLatest(REMOVE_POST_REQUEST, removePost);
}

export default function* postSaga() {
  yield all([
    fork(watchAddPost),
    fork(watchAddComment),
    fork(watchRemovePost),
  ]);
}
